il.SrSearchPDBlock={field:null,operator:1,init:function(){$(document).ready(function(){this.field=$("#srsearchpdblock_current_page_search"),this.field.on("input",this.onInput.bind(this))}.bind(this))},onInput:function(){const a=this.parseWords(el.innerText);$(".ilObjListRow").each(this.testObject.bind(this,a))},parseWords:function(a){return a.toLowerCase().split(/\s/).map(function(a){return a.trim()}).filter(function(a){return 0<a.length})},testObject:function(a,b){const c=this.parseWords(b.innerText);let d;if(0<a.length)switch(this.operator){case 1:d=this.testObjectAND(a,c);break;case 2:d=this.testObjectOR(a,c);break;default:d=!0;}else d=!0;b.style.display=d?"":"none"},testObjectAND:function(a,b){return a.every(function(a){return b.some(function(b){return-1!==b.indexOf(a)})})},testObjectOR:function(a,b){return a.some(function(a){return b.some(function(b){return-1!==b.indexOf(a)})})}};
