il.SrSearchPDBlock={field:null,operator:1,init:function(){$(document).ready(function(){this.field=$("#srsearchpdblock_current_page_search"),this.field.on("input",this.onInput.bind(this))}.bind(this))},onInput:function(){const a=this.parseWords(this.field.val());$(".ilObjListRow, .il-card, .tile").each(this.testObject.bind(this,a))},parseWords:function(a){return a.toLowerCase().split(/\s/).map(function(a){return a.trim()}).filter(function(a){return 0<a.length})},testObject:function(a,b,c){c.style.display="";const d=this.parseWords(c.innerText);let e;if(0<a.length)switch(this.operator){case 1:e=this.testObjectAND(a,d);break;case 2:e=this.testObjectOR(a,d);break;default:e=!0;}else e=!0;c.style.display=e?"":"none"},testObjectAND:function(a,b){return a.every(function(a){return b.some(function(b){return-1!==b.indexOf(a)})})},testObjectOR:function(a,b){return a.some(function(a){return b.some(function(b){return-1!==b.indexOf(a)})})}};
