il.SrSearchPDBlock={field:null,operator:1,init:function(){$(document).ready(function(){this.field=$("#srsearchpdblock_current_page_search"),this.field.on("input",this.onInput.bind(this))}.bind(this))},onInput:function(){const a=this.parseWords(this.field.val());$(".ilObjListRow").each(this.testObject.bind(this,a))},parseWords:function(a){return a.toLowerCase().split(/\s/).map(a=>a.trim()).filter(a=>0<a.length)},testObject:function(a,b,c){const d=this.parseWords($(c).text());let e;if(0<a.length)switch(this.operator){case 1:e=this.testObjectAND(a,d);break;case 2:e=this.testObjectOR(a,d);break;default:e=!0;}else e=!0;c.style.display=e?"":"none"},testObjectAND:function(a,b){let c=!0;for(const d of b)for(const b of a)if(-1===d.indexOf(b)){c=!1;break}return c},testObjectOR:function(a,b){let c=!1;for(const d of b)for(const b of a)if(-1!==d.indexOf(b)){c=!0;break}return c}};
