il.SrSearchPDBlock={field:null,operator:1,init:function(){$(document).ready(function(){this.field=$("#srsearchpdblock_current_page_search"),this.field.on("input",this.onInput.bind(this))}.bind(this))},onInput:function(){const a=this.field.val().toLowerCase(),b=a.split(/\s/).map(a=>a.trim()).filter(a=>0<a.length);$(".ilObjListRow").each(function(a,c){this.testObject(b,c)}.bind(this))},testObject:function(a,b){const c=b.innerText.toLowerCase();let d;if(console.log(a),0<a.length)switch(this.operator){case 1:d=!0;for(const b of a)if(-1===c.indexOf(b)){d=!1;break}break;case 2:d=!1;for(const b of a)if(-1!==c.indexOf(b)){d=!0;break}break;default:d=!0;}else d=!0;b.style.display=d?"":"none"}};
