il.SrSearchPDBlock={field:null,operator:1,init:function(){$(document).ready(function(){this.field=$("#srsearchpdblock_current_page_search"),this.field.on("input",this.onInput.bind(this))}.bind(this))},onInput:function(){const a=this.parseWords(this.field.val());$(".ilObjListRow").each(function(b,c){this.testObject(a,c)}.bind(this))},parseWords:function(a){return a.toLowerCase().split(/\s/).map(a=>a.trim()).filter(a=>0<a.length)},testObject:function(a,b){const c=this.parseWords(b.innerText);let d;if(console.log(a),0<a.length)switch(this.operator){case 1:d=!0;for(const b of c)for(const c of a)if(-1===b.indexOf(c)){d=!1;break}break;case 2:d=!1;for(const b of c)for(const c of a)if(-1!==b.indexOf(c)){d=!0;break}break;default:d=!0;}else d=!0;b.style.display=d?"":"none"}};
